package com.data.ss8.ra.controller;
import com.data.ss8.ra.model.Seeds;
import com.data.ss8.ra.model.UserBai8;
import com.data.ss8.ra.model.WarehouseSeeds;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.*;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

@Controller
@SessionAttributes("user")
public class GameController {
    private List<Seeds> seedsList = new ArrayList<>();
    private Map<Integer, WarehouseSeeds> warehouse = new HashMap<>();
    public GameController() {
        seedsList.add(new Seeds(1, "data:image/jpeg;base64,", 1000, "url1"));
        seedsList.add(new Seeds(2, "data:image/jpeg;base64,", 500, "url2"));
    }

    @GetMapping("/home")
    public String home(Model model, @ModelAttribute("user") UserBai8 user) {
        if (user == null || user.getUsername() == null) {
            return "redirect:/register";
        }
        return "home";
    }

    @GetMapping("/register")
    public String showRegister(Model model) {
        model.addAttribute("user", new UserBai8());
        return "register";
    }

    @PostMapping("/register")
    public String doRegister(@ModelAttribute("user") UserBai8 user) {
        return "redirect:/";
    }

    @GetMapping("/login")
    public String showLogin(Model model) {
        model.addAttribute("user", new UserBai8());
        return "login";
    }

    @PostMapping("/login")
    public String doLogin(@ModelAttribute("user") UserBai8 user, Model model) {
        return "redirect:/home";
    }

    @GetMapping("/shop")
    public String showShop(Model model) {
        model.addAttribute("seedsList", seedsList);
        return "shop";
    }

    @PostMapping("/buy")
    public String buySeed(@RequestParam int seedId, @RequestParam int quantity, @ModelAttribute("user") UserBai8 user) {
        Seeds seed = seedsList.stream().filter(s -> s.getId() == seedId).findFirst().orElse(null);
        if (seed != null && user.getBalance() >= seed.getPrice() * quantity) {
            user.setBalance(user.getBalance() - seed.getPrice() * quantity);
            WarehouseSeeds ws = warehouse.getOrDefault(seedId, new WarehouseSeeds());
            ws.setId(seedId);
            ws.setSeeds(seed);
            ws.setQuantity(ws.getQuantity() + quantity);
            warehouse.put(seedId, ws);
        }
        return "redirect:/shop";
    }

    @GetMapping("/warehouse")
    public String showWarehouse(Model model) {
        model.addAttribute("warehouseList", warehouse.values());
        return "warehouse";
    }

    @ModelAttribute("user")
    public UserBai8 setUpUser() {
        return new UserBai8();
    }
}