package com.data.ss8.ra.controller;

import com.data.ss8.ra.model.Product;
import com.data.ss8.ra.model.Question;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;

import java.util.ArrayList;
import java.util.List;
import java.util.Random;

@Controller
public class QuestionController {
    @GetMapping("Bai7")
    public String findAll(Model model) {
        List<Question> question = new ArrayList<Question>();
        question.add(new Question("táo", "data:image/jpeg;base64,"));
        question.add(new Question("lê", "data:image/jpeg;base64,"));
        question.add(new Question("bưởi", "data:image/jpeg;base64,"));
        Random random = new Random();
        int randomNumber = random.nextInt(3);
        model.addAttribute("question", question.get(randomNumber));
        return "bai7";
    }
}