package com.data.ss7.controller;
import com.data.ss7.model.Product;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestParam;

import java.util.ArrayList;
import java.util.List;

@Controller
public class ListController {
    private List<Product> getProductList() {
        List<Product> products = new ArrayList<>();
        products.add(new Product(1L, "Sản phẩm A", 100.0, 1, "Mô tả aaa", "data:image/jpeg;base64,"));
        products.add(new Product(2L, "Sản phẩm B", 200.0, 5, "Mô tả bbb", "data:image/jpeg;base64,"));
        products.add(new Product(3L, "Sản phẩm C", 300.0, 10, "Mô tả ccc", "data:image/jpeg;base64,"));
        return products;
    }

    @GetMapping("/products")
    public String showProductList(Model model) {
        model.addAttribute("products", getProductList());
        return "product_list";
    }

    @GetMapping("/product/details")
    public String showProductDetails(@RequestParam Long id, Model model) {
        for (Product product : getProductList()) {
            if (product.getId().equals(id)) {
                model.addAttribute("product", product);
                return "product_details";
            }
        }
        return "redirect:/products";
    }
}
